

class ServiceTemplateX {
	serviceName = (servicesLocal, featureNameSC) => `${servicesLocal}/${featureNameSC}.service.dart`;
	serviceTemplate = (featureNameSC, featureNamePC) =>             `
import 'package:meta/meta.dart';
import '../../main/protocols/i_${featureNameSC}.service.dart';
import '../protocols/i_${featureNameSC}.datasource.dart';
class ${featureNamePC}Service implements I${featureNamePC}Service {
	final I${featureNamePC}Datasource datasource;
	${featureNamePC}Service(
		{
			@required this.datasource
		}
	);

}
`
}

module.exports = ServiceTemplateX;