class UsecaseTemplate {
    usecaseLocal = (usecaseLocal, snackCase) => `${usecaseLocal}/${snackCase}.usecase.dart`;
    usecaseTemplate = (snackCase, pascalCase) => `
import 'package:dartz/dartz.dart';
import 'package:meta/meta.dart';

import '../interfaces/i_${snackCase}.usecase.dart';
import '../protocols/i_${snackCase}.service.dart';
class ${pascalCase}Usecase implements I${pascalCase}Usecase {
	final I${pascalCase}Service service;
	${pascalCase}Usecase(
		{
			@required this.service
		}
	);
}
`
}

module.exports = UsecaseTemplate;
